// Start with Response "Normal completion" 
var myDataType = Buffer.from([0,0]);
let myList = [];
let inData = msg.payload[1];

myList.push(myDataType);

// Cycle trough 0x0104 Request Message
let i = 0;
for (i = 0; i < inData.byteLength;i+=4) {

    // msg.payload[1][i]
    //var myData = Buffer.from([0, 0, 0]);
    let myData = Buffer.allocUnsafe(3);
    myData[0] = inData[i];
    let addr = inData.readInt16BE(i+1);

    // Look up data
    if (inData[i] == 128) {  // CIO 0x80

        if (addr == 0) {            // Physical Input
            myData.writeInt16BE(global.get("Physical_input"), 1);
        } else if (addr == 100) {   // Physical Output
            myData.writeInt16BE(global.get("Physical_output"), 1);
        }

    } else if (inData[i] == 130) {  // D 0x82

        if (addr == 100) {          // D100 - Hor_pos_act[-180,180]
            myData.writeInt16BE(global.get("Horizontal_pos_act"), 1);
        } else if (addr == 200) {   // D200 - Vert_pos_act[-30,30]
            myData.writeInt16BE(global.get("Vertical_pos_act"), 1);
        } else if (addr == 404) {   // D404 
            let myFocusPos = Buffer.allocUnsafe(4);  // Placeholder for the Float value [HiWord,LoWord]
            myFocusPos.writeFloatBE(global.get("Focus_Position"),0);
            let myFocusPosHi = myFocusPos.readUInt16BE(0); 
            myData.writeUInt16BE(myFocusPosHi, 1);
        } else if (addr == 405) {   // D405 
            let myFocusPos = Buffer.allocUnsafe(4);  // Placeholder for the Float value [HiWord,LoWord]
            myFocusPos.writeFloatBE(global.get("Focus_Position"), 0);
            let myFocusPosLo = myFocusPos.readUInt16BE(2);
            myData.writeUInt16BE(myFocusPosLo, 1);
        }

    } else if (inData[i] == 177) {  // W 0xB1

        if (addr == 15) {           // W15
                                              // W15.00 - Init_active
                                              // W15.01 - Con_flag
            myData.writeInt16BE(4, 1);  //myDataType[2] = 4;                    // W15.02/H150.00 - SL_mounted_0deg
                                              // W15.03/H150.01 - SL_mounted_90deg
                                              // W15.04/H150.02 - SL_mounted_180deg
                                              // W15.05/H150.03 - SL_mounted_-90deg
        }

    } else if (inData[i] == 178) {  // H 0xB2

        if (addr == 100) {          // H100 - SL_Version
            myData.writeInt16BE(global.get("SL_Version"), 1);
        } else if (addr == 150) {   // H150
            myData.writeInt16BE(global.get("H150"), 1);
        } else if (addr == 151) {   // H151
            myData.writeInt16BE(global.get("H151"), 1);
        } else if (addr == 254) {   // H254 - Horizontal_speed
            myData.writeUInt16BE(global.get("Horizontal_speed"), 1);
        } else if (addr == 256) {   // H256 - Vertical_speed
            myData.writeUInt16BE(global.get("Vertical_speed"), 1);
        } else if (addr == 260) {   // H260 - Slave_Speed_Hor
            myData.writeUInt16BE(global.get("H260"), 1);
        } else if (addr == 261) {   // H261 - Slave_Speed_Vert
            myData.writeUInt16BE(global.get("H261"), 1);
        } else if (addr == 270) {   // H270 - Vert_Pos_Init
            let myFocusPos = Buffer.allocUnsafe(4);  // Placeholder for the Float value [HiWord,LoWord]
            myFocusPos.writeFloatBE(global.get("H270"), 0);
            let myFocusPosHi = myFocusPos.readUInt16BE(0);
            myData.writeUInt16BE(myFocusPosHi, 1);
        } else if (addr == 271) {   // H271 
            let myFocusPos = Buffer.allocUnsafe(4);  // Placeholder for the Float value [HiWord,LoWord]
            myFocusPos.writeFloatBE(global.get("H270"), 0);
            let myFocusPosLo = myFocusPos.readUInt16BE(2);
            myData.writeUInt16BE(myFocusPosLo, 1);
        }

    }

    myList.push(myData);

}  //..for (i = 0;...

let telegram = Buffer.concat(myList);

return { payload: telegram, len: inData.byteLength };

