var lines = msg.payload.split("\n");
var data = [];

for(var i=0; i< lines.length; i++){
    if(lines[i].indexOf("interface eth0") != -1)
        break;
}

//check if interface eth0 is disabled
if(i < lines.length && lines[i].indexOf("#") >= 0) flow.set('disabled', true);
else flow.set('disabled', false);

node.send({topic:'interface eth0', payload:flow.get('disabled')})

i++;

for(i; i< lines.length; i++){

   //new interface, not eth0
   if(lines[i].indexOf("static") == -1)
    break;

    var tmp = lines[i].split("=");
    if(tmp.length==2){
        var prop = tmp[0].split(' ')[1].trim();
        var value = tmp[1].trim();

        flow.set(prop, value);
        node.send({topic:prop, payload:value})
    }
}

var properties = flow.get("properties");

var disabled = flow.get("disabled") ? '#' : '';

if(disabled == '#'){
    //enable interface (replace only first occurance)
    var command = `'0,/#interface eth0/s//interface eth0/g;' /etc/dhcpcd.conf`;
    node.send({payload: command})
}

for(var i=0;i<properties.length;i++){
    var p = properties[i];

    var old_value = flow.get(p);
    var new_value = flow.get(p + "_new") || old_value;

    if (old_value && ((old_value != new_value) || disabled == '#')) {
    //if (old_value && (old_value != new_value)) {
        //enable interface (replace only first occurance)
        var command = `'s?${disabled}static ${p}=${old_value}?static ${p}=${new_value}?g' /etc/dhcpcd.conf`;
        //var command = `'s?${p}=${old_value}?${p}=${new_value}?g' /etc/dhcpcd.conf`;

        node.send({payload: command})
    }
}

