function decimalToHex(d, padding) {

    if (d < 0) {
        if (padding == 2) {
            d = 0xFF + d + 1;
        } else if (padding == 4) {
            d = 0xFFFF + d + 1;
        }
    }

    var hex = Number(d).toString(16).toUpperCase();
    padding = typeof (padding) === "undefined" || padding === null ? padding = 2 : padding;

    while (hex.length < padding) {
        hex = "0" + hex;
    }

    return hex;
}

